define(function (require, exports, module) {
var Utils         = require("../../utils")

module.exports = (function() {
  var GenericClient = function() {};

  Utils.inherit(GenericClient, Utils.CustomEventEmitter);

  GenericClient.prototype.query = function(sql, cb) {
    this.sql = sql;
    var self = this;
    setTimeout(function() {
      cb(null, ["Asleep"], ["crack"]);
    }, 1000);
    return this;
  };

  GenericClient.prototype.setMaxListeners = function(n) {
    console.log("Setting maxListeners to : " + n);
  };

  GenericClient.prototype.end = function(cb) {
    console.log("Ending client...");
    cb();
  };

  GenericClient.prototype.destroy = function() {
    console.log("Destroying client...");
  };

  return GenericClient;
})();

});
